mapper.select:
	SELECT a.*,u.username,u.nickname FROM `cms_article` a,`cms_user` u 

	 a.`user_id`=u.`id`
	
		and	   channel_id = #{channelId,jdbcType=INTEGER}
	
	
		and  category_id = #{categoryId,jdbcType=INTEGER}
	
	
		and  user_id = #{userId,jdbcType=INTEGER}
	
	
		and status=#{status}
	
	
		and deleted=#{deleted}
	
	
		and hot=#{hot}
	
	
		and title like "%" #{title} "%"
	

order by created desc

select * from cms_article a	where a.id = (
SELECT id FROM `cms_article` b WHERE b.`channel_id`=#{channelId} AND b.id > #{id}  AND deleted=1 AND STATUS=1  ORDER BY `created` ASC LIMIT 1
)

select * from cms_article a	where a.id = (
SELECT id FROM `cms_article` b WHERE b.`channel_id`=#{channelId} AND b.id < #{id} AND deleted=1 AND STATUS=1  ORDER BY created DESC LIMIT 1
)
